package April6th;

public class ControlFlowSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int s=5;
		int n = s+0; //checking on while value
		switch (n)
		{
		case 5:
			System.out.println("n is same as s");
			break;
			
		case 6:
			System.out.println("n is 1 more than s");
			break;
			
		case 7:
			System.out.println("n is 2 more than s");
			break;
		}
		

	}

}
