package April7th;//Sum using while

public class SumWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		int s=0;
		int i=1;
		while (i<=n)
		{
			s = s+i;
			//System.out.println("Sum of 5 numbers: "+s);
			i++;
		}
		System.out.println("Sum of 5 numbers: "+s);
	}

}
