public class AQ25P4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=7;
		for (int i=1;i<=n;i++)
		{
			
			for (int j=1;j<=i;j++)
			{
				if ((i%2)==0) {
				break;}
				System.out.print("1");
				
			}
			
			System.out.println();
		}
		
		

	}

}
